sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	/* Create constants for table names and have function to query the data*/
    var wti = new GlideRecord("x_snc_wind_turbine_wind_turbine_incident");
    wti.addActiveQuery();
    wti.addQuery("cmdb_ci", current.sensorid);
    wti.addQuery("type", 2);
    wti.query();
	/* Validting to create the record or not*/
    if (!wti.next()) {
        if (current.temperature > 7 && current.temperature <= 7.5) {
            var gr = new GlideRecord("x_snc_wind_turbine_wind_turbine_incident");
            gr.initialize();
            gr.priority = 4;
            gr.active = true;
            gr.cmdb_ci = current.sensorid;
            gr.short_description = "Device : " + current.sensorid.name + " is running with temperature : " + current.temperature;
			gr.type=2;
            gr.insert();
        } else if (current.temperature > 7.5 && current.temperature <= 8) {
            /* you can move this creation part to a script include*/
            var gr1 = new GlideRecord("x_snc_wind_turbine_wind_turbine_incident");
            gr1.initialize();
            gr1.priority = 3;
            gr1.active = true;
            gr1.cmdb_ci = current.sensorid;
            gr1.short_description = "Device : " + current.sensorid.name + " is running with temperature : " + current.temperature;
			gr1.type=2;
            gr1.insert();
        } else if (current.temperature > 8) {
            /* you can move this creation part to a script include*/
            var gr2 = new GlideRecord("x_snc_wind_turbine_wind_turbine_incident");
            gr2.initialize();
            gr2.priority = 1;
            gr2.active = true;
            gr2.cmdb_ci = current.sensorid;
            gr2.short_description = "Device : " + current.sensorid.name + " is running with temperature : " + current.temperature;
			gr2.type=2;
            gr2.insert();
        }
    }

})(current, previous);
