sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, imports}) {
    const openRecord = function (table, sysID) {
        console.log('#######');
        var redirectPayload = {
            redirect: true,
            route : 'recordpage',
            fields: {
            sysId: sysID,
            table: table
        }
    };

        api.emit("NAV_ITEM_SELECTED", redirectPayload);
    };
    openRecord(event.payload.payload.table,   event.payload.payload.sysId)
}
