sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    const openList = function (table, query, listTitle) {
        var redirectPayload = {
            redirect: false,
            route : 'simplelist',
            fields: {
                table: table
            },
            params: {
                query: query,
                listTitle: listTitle
            },
            title: listTitle,
            targetRoute: 'current'
    };

       api.emit("NAV_ITEM_SELECTED", redirectPayload);
   };

   openList(event.payload.params.table, event.payload.params.query, event.payload.params.listTitle);
}
